import os
#N petrol pumps on a circle numbered 0 to (N-1)(both inclusive)
#petrolpumps= [N, [amount of petrol of that petrol pump, the distance to the next petrol pump],[...],[...],[N-1]]
#The truck will move one kilometer for each litre of petrol
#Calculate the first point from where the truck will be able to complete the circle
def truckTour(petrolpumps):
    petrolpumps.pop(0)
    n=len(petrolpumps)
    pump_index=0
    tank=0
    for i in range(n):
        ptr, dst= petrolpumps[i]
        tank+= ptr-dst
        if tank<0:
            pump_index=i+1
            tank=0
            
    return pump_index
